import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
  return (
    <button
      className="square"
      onClick={props.onClick}
    >
      {props.value}
    </button>
  );
}

class Board extends React.Component {
  renderSquare(i) {
    return <Square
      key={i}
      value={this.props.squares[i]}
      onClick={() => this.props.onClick(i)}
    />;
  }

  createRows() {
    let rows = [];

    for(let line = 0; line < 3; line++) {
      let cells = [];

      for(let cell = 0; cell < 3; cell++) {
        cells.push(this.renderSquare(3 * line + cell));
      }

      rows.push(<div className="board-row" key={line}>{cells}</div>)
    }
    return rows;
  }

  render() {
    return (
      <div>
        {this.createRows()}
      </div>
    )
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [{
        squares: Array(9).fill(null),
        moveSpot: ''
      }],
      xIsNext: true,
      stepNumber: 0,
    };
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: (step%2) === 0
    })
  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();
    if(calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      history: history.concat([{
        squares: squares,
        moveSpot: i
      }]),
      xIsNext: !this.state.xIsNext,
      stepNumber: history.length
    });
  }

  render() {
    const history = this.state.history;
    const currentMove = history[this.state.stepNumber];
    const moveSpot = currentMove.moveSpot;
    const gameEndMessage = calculateWinner(currentMove.squares);

    const movesButtons = history.map((move, moveNumber) => {
      const description = moveNumber ?
        'Go to move #' + moveNumber + ' (' +moveSpot +')':
        'Go to game start';

      if(moveNumber === this.state.stepNumber)
      {
        return (
          <li key={moveNumber}>
            <button onClick={() => this.jumpTo(moveNumber)}><b>{description}</b></button>
          </li>
        )
      } else {
        return (
          <li key={moveNumber}>
            <button onClick={() => this.jumpTo(moveNumber)}>{description}</button>
          </li>
        )
      }
    });

    let status;
    if(gameEndMessage) {
      status = gameEndMessage;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }

    return (
      <div className="game">
        <div className="game-board">
          <Board 
            key='board'
            squares={currentMove.squares}
            onClick={(i) => this.handleClick(i)}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{movesButtons}</ol>
        </div>
      </div>
    );
  }
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return 'Winner: ' +squares[a];
    }
  }

  for (let cell = 0; cell < squares.length; cell++) {
    if(!squares[cell]) {
      return null;
    }
  }

  return 'It\'s a draw!';
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
